import { useState, useEffect } from "react";

const useImage = ({
  src
}) => {
  const {
    0: loaded,
    1: setLoaded
  } = useState(false);
  const {
    0: error,
    1: setError
  } = useState(false);
  useEffect(() => {
    const img = new Image();
    img.src = src;

    img.onload = () => setLoaded(true);

    img.onerror = () => setError(true);
  }, [src]);
  return {
    loaded,
    error
  };
};

export default useImage;
